function authorization (req, res, next) {
    var token=localStorage.getItem("token");
    if(token){
       res= true;
    }
    else{
        res.status(403);
        console.log("Necesitas Iniciar Sesion");
        res= false;
    }
    
}

module.exports = {authorization};
