import React, { Component,useState,useEffect } from 'react';
import { Link } from 'react-router-dom';
import './../Navbar.css';
import axios from 'axios';


export default class Navbar extends Component {
  constructor(props){
    super(props);
    const [click, setClick] = useState(false);
    const [button, setButton] = useState(true);
  
    const handleClick = () => setClick(!click);
    const closeMobileMenu = () => setClick(false);
    useEffect(() => {
      this.showButton();
    }, []);

    window.addEventListener('resize', this.showButton);
  }
  
    showButton = () => {
    if (window.innerWidth <= 960) {
      this.setButton(false);
    } else {
      this.setButton(true);
    }
  };
  

  eventoLogOut=()=>{
    localStorage.clear();
    this.closeMobileMenu();
  }

  render() {
    return (
      <nav className="navbar navbar-dark bg-dark navbar-expand-lg">
        <Link to="/" className="navbar-brand" onClick={this.closeMobileMenu}>Consultorio Medico
          <i class="fas fa-stethoscope"/>
        </Link>
        <div className='menu-icon' onClick={this.handleClick}>
            <i className={this.click ? 'fas fa-times' : 'fas fa-bars'} />
          </div>
        <div className="collpase navbar-collapse">
        <ul className={this.click ? 'nav-menu active' : 'nav-menu'}>
          <li className="navbar-item">
          <Link to="/turnos" className="nav-link" onClick={this.closeMobileMenu}>Turnos</Link>
          </li>
          <li className="navbar-item" id="sacarTurno">
          <Link to="/createTurno" className="nav-link" onClick={this.closeMobileMenu}>Sacar turno</Link>
          </li>
          <li className="navbar-item">
          <Link to="/users" className="nav-link" onClick={this.closeMobileMenu}>Usuarios</Link>
          </li>
          <li className="navbar-item">
          <Link to="/createUser" className="nav-link" onClick={this.closeMobileMenu}>Crear usuario</Link>
          </li>
          <li className="navbar-item">
          <Link to="/recetas" className="nav-link" onClick={this.closeMobileMenu}>Recetas</Link>
          </li>
          <li className="navbar-item">
          <Link to="/createReceta" className="nav-link" onClick={this.closeMobileMenu}>Cargar Receta</Link>
          </li>
          <li className="navbar-item">
          <Link to="/historialList" className="nav-link" onClick={this.closeMobileMenu}>Historiales</Link>
          </li>
          <li className="navbar-item">
          <Link to="/crearHistorial" className="nav-link" onClick={this.closeMobileMenu}>Crear historial</Link>
          </li>
          <li className="navbar-item">
          <Link to="/" className="nav-link" onClick={this.eventoLogOut}>Salir</Link>
          </li>
        </ul>
        </div>
      </nav>
    );
  }
}
