import React from 'react';
import {Grid, TextField, Button, InputAdornment,Select} from '@material-ui/core';
import { AccountCircle, LockRounded} from '@material-ui/icons';
//import { makeStyles } from '@material-ui/core/styles';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
//import FormHelperText from '@material-ui/core/FormHelperText';
//import FormControl from '@material-ui/core/FormControl';


import { Link } from 'react-router-dom';



export default function Login() {
  const [usuario, setUsuario] = React.useState('');
  const [name,setName]=React.useState("");
  const [pass,setPass]=React.useState("");

  //controladores de eventos:  
  const handleChange = (event) => {
    setUsuario(event.target.value);
  };
  const handleChange2 = (event) => {
    setName(event.target.value);
    console.log(name);
  };
  const handleChange3 = (event) => {
    setPass(event.target.value);
    console.log(name);
  };
  

  return (
        <div>
          <Grid container style={{minHeight: '100vh'}}>

            <Grid items xs={12} sm={6}>
              <img src="/images/Intranet.jpg" 
              style={{width:'100%', height: '100%', objectFit:'Cover'}} 
              alt="brand" />

            </Grid>
            <Grid container item xs={12} sm={6} 
            alignItems="center" 
            direction="column"
            justify="space-between" 
            style={{padding: 10, background: "white"}}>
              <div/>
              <div style={{display:'flex', flexDirection:"column", maxWidth:400, minWidth:300}}>                
                  <Grid container justify="center">
                    <img src="/images/LogoCentro.png" width={200} alt="logo" />
                  </Grid>
                  <div style={{height:20}}/>
                  <TextField
                   label="Usuario"
                   margin="normal" 
                   input={name}
                   onChange={handleChange2}
                   InputProps={{startAdornment: <InputAdornment position="start"><AccountCircle /></InputAdornment>}}/>
                  <TextField
                   label="Contraseña" 
                   margin="normal" 
                   type="password"
                   input={pass}
                   onChange={handleChange3}
                   InputProps={{startAdornment: <InputAdornment position="start"><LockRounded /></InputAdornment>}}/>
                  <div style={{height:20}}/>
                  
                  
                  <div style={{height:20}} />
                  <Link to ="./turnos">
                 < Button color="primary" variant="contained"
                 
                 >
                    Iniciar Sesión 
                    
                  </Button>
                  </Link>
                  <br></br>

                  
                </div>  
              <div />            
            </Grid>
           
          </Grid>

        </div>
    
  );

}

 function Redirigir(valor){
  alert(valor);
}
